//fetch the sheet
Sheet sh = workbook1.getSheet("Sheet1")

def charset = (('A'..'Z') + ('0'..'9')).join()
def length = 9
def randomString = RandomStringUtils.random(length, charset.toCharArray())
def email = randomString + "@gmail.com"

def firstName = sh.getCell(0, 1).getContents()
def lastName = sh.getCell(1, 1).getContents()
def dob = sh.getCell(2, 1).getContents()

log.info email
log.info firstName
log.info lastName
log.info dob

testRunner.testCase.setPropertyValue("email", email)
testRunner.testCase.setPropertyValue("firstName", firstName)
testRunner.testCase.setPropertyValue("lastName", lastName)
testRunner.testCase.setPropertyValue("dob", dob)

