//Define Groovy Utils and holder for validating the XML reponse content
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)

//Define the NameSpace
holder.namespaces["ns1"] = "http://soap.upskills.in/soap/default?services=customerCustomerRepositoryV1"

//Get the Value of the Node 'AddResult' and assign to a variable
def result = holder.getNodeValue("//result")

//print the value of the result in the Output panel
log.info "The result value for integers is " + result

//Comparing the value to print 'Pass' or 'Fail'
if(result=="true")
{ log.info "Pass" }
else
{ log.info "fail"}
